import styled from "styled-components";
import "@fontsource/space-grotesk";

export const ContainerProjects = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: row;
    margin: 0 10% 10% 10%;
`;

export const SubContainerProjects = styled.div`
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    width: 50%;
    height: 200px;
`;

export const Divider = styled.div`
    height: 5px;
    width: 30%;
    background-color: #37D892;
    background-image: linear-gradient(to right, #37D892 , #5825C7);
`;

export const SubTitle = styled.span`
    font-family: 'Inter', sans-serif;
    width: 30%;
    font-style: normal;
    font-weight: 700;
    color: ${props => props.theme.colors.text};
    font-size: 25px;
`;

export const SubTitleColor = styled.span`
    font-family: 'Inter', sans-serif;
    font-style: normal;
    font-weight: 700;
    color: #37D892;
    font-size: 25px;
`;

export const ContainerButtons = styled.button`

`;
