import React, { useContext } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';

import ProjectsData from '../../projects.json';

import {
    ContainerProjects,
    SubContainerProjects,
    Divider,
    SubTitle,
    SubTitleColor,
    ContainerButtons,
    ButtonArrows,
    ButtonGit,
    Carousel,
    Project,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools
} from './styles';

import { FiArrowRight, FiArrowLeft, FiExternalLink } from "react-icons/fi";
interface Props {
    toggleTheme(): void;
}

const Projects: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    return (
        <ContainerProjects id="portifolio">
            <SubContainerProjects>
                <Divider />
                <SubTitle>
                    Meus projetos
                    <SubTitleColor> pessoais</SubTitleColor>
                </SubTitle>

                <ContainerButtons>
                    <ButtonArrows>
                        <FiArrowLeft color='#37D892' size={25} />
                    </ButtonArrows>
                    <ButtonArrows>
                        <FiArrowRight color='#37D892' size={25} />
                    </ButtonArrows>
                </ContainerButtons>
            </SubContainerProjects>

            <Carousel>
                {ProjectsData.map(props => {
                    return (
                        <Project>
                            <Image />

                            <ContainerTitle>
                                <TitleProject>TCC - Professor</TitleProject>
                                <FiExternalLink color={colors.text} size={25} />
                            </ContainerTitle>

                            <Description></Description>
                            <Tools></Tools>
                        </Project>
                    )
                })}

            </Carousel>

            {/**
             * 
            <ButtonGit>
                Ver todos os repositórios no Github
            </ButtonGit>
             */}
        </ContainerProjects>
    );
}

export default Projects;
