import styled from "styled-components";
import "@fontsource/space-grotesk";

export const ContainerWaves = styled.div`
    display: flex;
    justify-content: space-between;
    flex-direction: row;
    margin-top: -11%;
    gap: 100px;
`;

export const ContainerArrow = styled.div`
    display: flex;
    justify-content: space-between;
    flex-direction: row;
    margin-top: 30%;
    gap: 100px;
`;
