import React, { useContext, useState } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import ProjectsData from '../../projectsdata';

import {
    ContainerProjects,
    SubContainerProjects,
    SubTitle,
    ButtonSeeMore,
    TextSeeMore,
    ContainerAllProjects,
    Project,
    BoxImage,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools,
} from './styles';

import Media from 'react-media';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { FiExternalLink } from "react-icons/fi";

interface Props {
    toggleTheme(): void;
}

const Projects: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    AOS.init();
    const [ controller, setController ] = useState(false);

    function seeMore(){
        setController(!controller);
    }
    let teste;

    if(controller == true){
        teste = Infinity;
    }else{
        teste = 3
    }
    
    return (
        <ContainerProjects id="projetos">
            <SubContainerProjects>
                <SubTitle>
                    Projetos pessoais
                </SubTitle>

                <ButtonSeeMore onClick={seeMore}>
                    <TextSeeMore >{controller ? "Ver menos" : "Ver mais"}</TextSeeMore>
                </ButtonSeeMore>
            </SubContainerProjects>

            <ContainerAllProjects>
                {ProjectsData.slice(0, teste).map((item) => {
                    const { id, img, title, description, tool, link } = item;
                    return (
                        <Project key={id} data-aos="zoom-in">
                            <BoxImage>
                                <Image src={img} />
                            </BoxImage>

                            <ContainerTitle>
                                <TitleProject>{title}</TitleProject>
                                <a href={link}><FiExternalLink color={colors.text} size={25} /></a>
                            </ContainerTitle>

                            <Description>{description}</Description>
                            <Tools>{tool}</Tools>
                        </Project>
                    )
                })}
            </ContainerAllProjects>
        </ContainerProjects>
    );
}

export default Projects;
