    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: row;
    margin: 0 10%;
    height: 20vh;
`;

export const SubContainerAbout = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: column;
`;

export const Divider = styled.div`
