import React, { useContext, useState, useEffect } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import axios from 'axios';

import { BiSearch } from "react-icons/bi";

import { ContainerContent, Header, TitlePokemon, IdPokemon, ContainerPokemon, ContainerTexts, ContainerImage } from './styles';

import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
    useParams
} from "react-router-dom";

interface Props {
    toggleTheme(): void;
}

interface Pokemon {
    name: string;
    game_index: string;
    sprite: string;
}

const Content: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    const [pokemon, setPokemon] = useState<Pokemon>();

    const { url } = useParams();
    
    const teste = '';

    if(url === ''){
        teste = url;
    }

    useEffect(() => {
        async function fetchData() {
            const res = await fetch(url);
            const data = await res.json();
            setPokemon({ name: data.name, game_index: data.id, sprite: data.sprites.front_default });
        }
        fetchData();
    }, []);

    return (
        <ContainerContent>
            <Header></Header>

            {pokemon ? (
                <ContainerPokemon>
                    <ContainerTexts>
                        <IdPokemon>#{pokemon.game_index}</IdPokemon>
                        <TitlePokemon>{pokemon.name}</TitlePokemon>
                    </ContainerTexts>

                    <ContainerImage>
                        <img src={pokemon.sprite} alt={pokemon.name} width='100%'/>
                    </ContainerImage>
                </ContainerPokemon>
            ) : (
                <ContainerPokemon>
                    <ContainerTexts>
                        <IdPokemon>Carregando...</IdPokemon>
                        <TitlePokemon>Carregando...</TitlePokemon>
                    </ContainerTexts>

                    <ContainerImage>
                    </ContainerImage>
                </ContainerPokemon>
            )}

        </ContainerContent >
    );
}

export default Content;
