import styled from "styled-components";
import "@fontsource/space-grotesk";

export const ContainerProjects = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: row;
    margin: 0 10% 10% 10%;
`;

export const SubContainerProjects = styled.div`
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    width: 45%;
    height: 200px;
`;

export const Divider = styled.div`
    height: 5px;
    width: 30%;
    background-color: #37D892;
    background-image: linear-gradient(to right, #37D892 , #5825C7);
`;

export const SubTitle = styled.span`
    font-family: 'Inter', sans-serif;
    width: 30%;
    font-style: normal;
    font-weight: 700;
    color: ${props => props.theme.colors.text};
    font-size: 25px;
`;

export const SubTitleColor = styled.span`
    font-family: 'Inter', sans-serif;
    font-style: normal;
    font-weight: 700;
    color: #37D892;
    font-size: 25px;
`;

export const ContainerButtons = styled.div`
    display: flex;
    flex-direction: row;
    gap: 15px;
`;

export const ButtonArrows = styled.button`
    display: flex;
    background: ${props => props.theme.colors.background};
    padding: 20px;
    box-shadow: ${props => props.theme.colors.shadow};
    border-radius: 100px;
`;

export const ButtonGit = styled.a`
    display: flex;
    background: #37D892;
    padding: 20px 40px;
    justify-content: center;
    align-items: center;
    border-radius: 100px;
    width: fit-content;
`;

export const Carousel = styled.div`
    display: flex;
    width: 70%;
    justify-content: space-between;
    overflow-x: auto;
    scroll-behavior: smooth;
`;

export const Project = styled.div`
    display: flex;
    background: ${props => props.theme.colors.background};
    box-shadow: ${props => props.theme.colors.shadow};
    width: 450px;
    flex-direction: column;
`;

export const Image = styled.div`

`;

export const ContainerTitle = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    padding: 30px;
`;

export const TitleProject = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 700;
    color: ${props => props.theme.colors.text};
    font-size: 25px;
`;

export const Description = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    color: ${props => props.theme.colors.text};
    margin: 0 30px;
    text-align: justify;
`;

export const Tools = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 400;
    color: ${props => props.theme.colors.text};
    margin: 0 30px 30px 30px;
`;
