import React, { useContext } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';

import { ContainerAbout, Image, SubContainerAbout, Divider, SubTitle, SubTitleColor, Description, ContactButton, TextButton } from './styles';
import { FiArrowRight } from "react-icons/fi";
interface Props {
    toggleTheme(): void;
}

const About: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    return (
        <ContainerAbout id="sobre">
            <Image src='' alt="Gabriel Barbosa" />

            <SubContainerAbout>
                <Divider />
                <SubTitle>
                    Um pouco sobre
                    <SubTitleColor> mim</SubTitleColor>
                </SubTitle>

                <Description>
                    aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
                    
                </Description>
                <ContactButton href="#contato">
                    <TextButton>
                        Contato
                    </TextButton>
                    <FiArrowRight color='#fff' size={17} />
                </ContactButton>
            </SubContainerAbout>
        </ContainerAbout>
    );
}

export default About;
