import React, { useContext, useState, useEffect } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import axios from 'axios';

import { BiSearch } from "react-icons/bi";

import { ContainerContent, Header, TitlePokemon, IdPokemon, ContainerPokemon, ContainerTexts, ContainerImage, ContainerGrid, ContainerDatas, TextTitleDatas, SubContainerDatas, StatusBox, TextSubDatas } from './styles';

import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
    useParams
} from "react-router-dom";

interface Props {
    toggleTheme(): void;
}

interface Pokemon {
    name: string;
    game_index: string;
    sprite: string;

    hp: string;
    attack: string;
    defense: string;
    special_attack: string;
    special_defense: string;
    speed: string;
}

const Content: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    const [pokemon, setPokemon] = useState<Pokemon>();

    const { url } = useParams();

    useEffect(() => {
        async function fetchData() {
            const res = await fetch(url === undefined ? `https://pokeapi.co/api/v2/pokemon/1` : `https://pokeapi.co/api/v2/pokemon/${url}`);
            const data = await res.json();
            setPokemon({ 
                name: data.name, 
                game_index: data.id, 
                sprite: data.sprites.other?.["official-artwork"].front_default,
                hp : data.stats
            });
        }
        fetchData();
    }, [url]);


    return (
        <ContainerContent>
            <Header></Header>

            {pokemon ? (
                <ContainerPokemon>
                    <ContainerTexts>
                        <IdPokemon>#{pokemon.game_index}</IdPokemon>
                        <TitlePokemon>{pokemon.name}</TitlePokemon>
                    </ContainerTexts>

                    <ContainerGrid>
                        <ContainerImage>
                            <img src={pokemon.sprite} alt={pokemon.name} width='100%' style={{ imageRendering: 'pixelated' }} />
                        </ContainerImage>

                        <ContainerDatas>
                            <TextTitleDatas>Status</TextTitleDatas>

                            <SubContainerDatas>
                                <StatusBox>
                                    <TextSubDatas>HP: {pokemon.hp}</TextSubDatas>
                                </StatusBox>
                            </SubContainerDatas>
                        </ContainerDatas>
                    </ContainerGrid>

                </ContainerPokemon>
            ) : (
                <ContainerPokemon>
                    <ContainerTexts>
                        <IdPokemon>Carregando...</IdPokemon>
                        <TitlePokemon>Carregando...</TitlePokemon>
                    </ContainerTexts>

                    <ContainerImage>
                    </ContainerImage>
                </ContainerPokemon>
            )}

        </ContainerContent >
    );
}

export default Content;
