import React, { useContext } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import { Link } from 'react-router-dom';
import ProjectsData from '../../projects.json';

import {
    ContainerProjects,
    ContainerHorizontal,
    SubContainerProjects,
    Divider,
    SubTitle,
    SubTitleColor,
    ContainerButtons,
    ButtonArrows,
    ButtonGit,
    Projects,
    Project,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools,
    AllProjects,
    TextButton
} from './styles';

import Media from 'react-media';

import { FiArrowRight, FiArrowLeft, FiExternalLink } from "react-icons/fi";
import { Swiper, SwiperSlide, useSwiper } from 'swiper/react';
import { Pagination, Navigation, Autoplay } from 'swiper';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
interface Props {
    toggleTheme(): void;
}

const ProjectsAll: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    const swiper = useSwiper();

    return (
        <ContainerProjects id="portifolio">
            <ContainerHorizontal>
                <SubContainerProjects>
                    <Divider />
                    <SubTitle>
                        Meus projetos
                        <SubTitleColor> pessoais</SubTitleColor>
                    </SubTitle>
                </SubContainerProjects>

                <Projects>
                    {ProjectsData.map((item) => {
                        const { id, img, title, description, tool, link } = item;
                        return (
                            <Project key={id}>
                                <Image />

                                <ContainerTitle>
                                    <TitleProject>{title}</TitleProject>
                                    <a href={link}><FiExternalLink color={colors.text} size={25} /></a>
                                </ContainerTitle>

                                <Description>{description}</Description>
                                <Tools>{tool}</Tools>
                            </Project>
                        )
                    })}
                </Projects>
            </ContainerHorizontal>

            <ContainerHorizontal>
                <AllProjects>
                    <TextButton>
                        Ver todos repositórios no GitHub
                    </TextButton>
                </AllProjects>

            </ContainerHorizontal>

        </ContainerProjects>
    );
}

export default ProjectsAll;
