import styled from "styled-components";
import "@fontsource/space-grotesk";

export const ContainerContact = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    margin: 0 10%;
    height: 30vh;
`;

export const TextTitle = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 700;
    color: ${props => props.theme.colors.text};
    font-size: 50px;
`;

export const SubTextTitle = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    color: ${props => props.theme.colors.text};
    font-size: 20px;
    width: 23%;
    text-align: center;
    margin-top: 10px;
`;

export const SubContainerContact = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-around;
`;

export const BoxContact = styled.a`
    display: flex;
    flex-direction: row;
    align-items: center;
    border: 1px solid #37D892;
    border-radius: 100px;
    padding: 10px;
`;

export const Icon = styled.div`
    width: 30px;
    height: 30px;
    background-color: #909090;
    border-radius: 100px;
`;

export const BoxTexts = styled.a`
    display: flex;
    flex-direction: column;
    
`;

export const TitleContact = styled.a`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 700;
    font-size: 15px;
    color: ${props => props.theme.colors.text};
`;

export const LinkText = styled.a`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 10px;
    color: ${props => props.theme.colors.text};
    width: 50px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
`;

export const BoxCV = styled.a`
    display: flex;
    flex-direction: row;
    align-items: center;
    border: 1px solid #37D892;
    background: #37D892;
    border-radius: 100px;
    padding: 10px;
`;

export const TitleContactCV = styled.a`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 700;
    font-size: 15px;
    color: ${props => props.theme.colors.text};
`;

export const LinkTextCV = styled.a`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 10px;
    color: ${props => props.theme.colors.text};
`;
