import React, { useContext, useState } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import ProjectsData from '../../projectsdata';

import {
    ContainerProjects,
    SubContainerProjects,
    SubTitle,
    ButtonSeeMore,
    TextSeeMore,
    ContainerAllProjects,
    Project,
    BoxImage,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools,
} from './styles';

interface Props {
    toggleTheme(): void;
}

const Projects: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    const [ controller, setController ] = useState(3);

    function seeMore(){
        setController(Infinity);
    }
    
    return (
        <ContainerProjects id="projetos">
            <SubContainerProjects>
                <SubTitle>
                    Projetos pessoais
                </SubTitle>

                <ButtonSeeMore>
                    <TextSeeMore onClick={seeMore}>Ver mais</TextSeeMore>
                </ButtonSeeMore>
            </SubContainerProjects>

            <ContainerAllProjects>
                {ProjectsData.slice(0, controller).map((item) => {
                    const { id, img, title, description, tool, link } = item;
                    return (
                        <Project key={id}>
                            <BoxImage>
                                <Image src={img} />
                            </BoxImage>

                            <ContainerTitle>
                                <TitleProject>{title}</TitleProject>
                                <a href={link}><FiExternalLink color={colors.text} size={25} /></a>
                            </ContainerTitle>

                            <Description>{description}</Description>
                            <Tools>{tool}</Tools>
                        </Project>
                    )
                })}
            </ContainerAllProjects>
        </ContainerProjects>
    );
}

export default Projects;
