import React, { useState, useEffect } from 'react'
import { ContainerContent, Header, TitlePokemon, IdPokemon, Img, ContainerPokemon, ContainerTexts, ContainerImage, ContainerGrid, ContainerDatas, TextTitleDatas, SubContainerDatas, StatusBox, TextSubDatas, BoxProgresos, BoxBorder } from './styles';

import GlobalStyle from '../styles/global';
import Sidebar from '../components/Content';

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams
} from "react-router-dom";

interface Pokemon {
  name: string;
  game_index: string;
  sprite: string;

  stats: Array<{ base_stat: string }>;

  weight: number;
  height: number;
}

function Home() {
  const [pokemon, setPokemon] = useState<Pokemon>();
  const { url } = useParams();

  useEffect(() => {
    async function fetchData() {
      const res = await fetch(url === undefined ? `https://pokeapi.co/api/v2/pokemon/1` : `https://pokeapi.co/api/v2/pokemon/${url}`);
      const data = await res.json();
      setPokemon({
        name: data.name,
        game_index: data.id,
        sprite: data.sprites.other?.["official-artwork"].front_default,
        stats: data.stats.map((stat: any) => stat.base_stat),
        weight: data.weight,
        height: data.height
      });
    }
    fetchData();
  }, [url]);

  return (
    <div className='App'>
      <GlobalStyle />

      <Sidebar/>

      <Content/>
    </div>
  );
}

export default Home;
