import styled from "styled-components";

export const ContainerSidebar = styled.div`
    display: flex;
    width: 400px;
    background-color: #CC0033;  
    padding: 30px;
    flex-direction: column;
    gap: 20px;
    max-height: calc(100vh);
    overflow-y: auto;
    &::-webkit-scrollbar {
        width: 10px;
    }

    /* Track */
    &::-webkit-scrollbar-track {
        background: #CC0033;
    }

    /* Handle */
    &::-webkit-scrollbar-thumb {
        background: #8A0023;
    }

    /* Handle on hover */
    &::-webkit-scrollbar-thumb:hover {
        background: #8A0023;
    }
`;

export const ContainerPokeball = styled.div`
    display: flex;
    height: 15vh;
    width: 100%;
    justify-content: center;
    align-items: center;
`;

export const Svg = styled.svg`
    filter: drop-shadow(0 0 0.75rem rgb(0 0 0 / 0.4));
`;

export const ContainerTitle = styled.div`
    display: flex;
    width: 100%;
    flex-direction: column;
    gap: 5px;
`;

export const Title = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 30px;
    color: #fff;
`;

export const SubTitle = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 15px;
    color: #FFB8B8;
`;

export const InputText = styled.input`
    height: 50px;
    width: 100%;
    border-radius: 5px;
    background-color: #8A0023;
    border: none;
    padding-left: 20px;

    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 13px;
    color: #FFB8B8;

    &::placeholder{
        color: #FFB8B8;
    }
`;

export const ContainerInput = styled.div`
    display: flex;
    align-items: center;
`;

export const GridPokemons = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 20px;
`;

export const BoxPokemon = styled.div`
    display: flex;
    flex-direction: column;
    width: 100%;
    height: 100px;
    background-color: #000;
    border-radius: 5px;
    padding: 20px;
`;

export const TitlePokemon = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 20px;
    color: #fff;
`;

export const IdPokemon = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 15px;
    color: #1C1C1C;
`;
