import styled from "styled-components";
import "@fontsource/space-grotesk";

export const ContainerAbout = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: row;
    margin: 0 10%;
    height: 20vh;
`;

export const Image = styled.img`
    width: 45%;
    height: 100px;
    background-color: #000;
`;

export const SubContainerAbout = styled.div`
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    width: 50%;
`;

export const Divider = styled.div`
    height: 5px;
    width: 30%;
    background-color: #37D892;
    background-image: linear-gradient(to right, #37D892 , #5825C7);
`;

export const SubTitle = styled.span`
    font-family: 'Inter', sans-serif;
    width: 30%;
    font-style: normal;
    font-weight: 700;
    color: ${props => props.theme.colors.text};
    font-size: 25px;
`;

export const SubTitleColor = styled.span`
    font-family: 'Inter', sans-serif;
    font-style: normal;
    font-weight: 700;
    color: #37D892;
    font-size: 25px;
`;

export const Description = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    color: ${props => props.theme.colors.text};
    font-size: 15px;
    text-align: justify; 
    line-break: anywhere;
`;

export const ContainerContactButton = styled.div`
    display: flex;
    height: 30px;
    background-color: #37D892;
`;

export const ContactButton = styled.button`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 700;
    color: #fff;
    text-align: center;
    border-radius: 5px
`;
