import React, { useContext, useState, useEffect } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import axios from 'axios';

import { BiSearch } from "react-icons/bi";

import { ContainerContent, Header, TitlePokemon, IdPokemon, ContainerTexts, ContainerImage } from './styles';

interface Props {
    toggleTheme(): void;
}

interface Pokemon {
    url: string;
    name: string;
    sprite: string;
    game_index: string;
    types: Array<{ name: string }>;
}

const Content: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    const [pokemonList, setPokemonList] = useState<Pokemon[]>([]);
    const [offset, setOffset] = useState(0);
    const [hasMore, setHasMore] = useState(true);

    useEffect(() => {
        async function fetchData() {
            const res = await fetch('https://pokeapi.co/api/v2/pokemon/1');
            const data = await res.json();
            const pokemonData = data.results;
            if (!pokemonData.length) {
                setHasMore(false);
                return;
            }
            const newPokemonList = await Promise.all(res.map(async (pokemon: any) => {
                const res = await fetch(pokemon.url);
                const data = await res.json();
                return { url: pokemon.url, name: pokemon.name, sprite: data.sprites.front_default, game_index: data.id, types: data.types.map((type: any) => type.type.name) };
            }));
            setPokemonList(prevPokemonList => [...prevPokemonList, ...newPokemonList]);
        }

        fetchData();
    }, [offset]);

    return (
        <ContainerContent>
            <Header></Header>
            <ContainerTexts>
                <IdPokemon>#01</IdPokemon>
                <TitlePokemon>Baulbasar</TitlePokemon>
            </ContainerTexts>
            <ContainerImage>

            </ContainerImage>
        </ContainerContent >
    );
}

export default Content;
