import React from 'react';
import { ThemeProvider, DefaultTheme } from 'styled-components';
import usePersistedState from './utils/usePersistedState';

import light from './styles/themes/light';
import dark from './styles/themes/dark';

import GlobalStyle from './styles/global';
import Presentation from './components/Presentation';
import Particlescom from './components/Particlescom';
import About from './components/About';
import Technologies from './components/Technologies';
import Projects from './components/Projects';
import Contact from './components/Contact';
import Footer from './components/Footer';

function App() {
  const [theme, setTheme] = usePersistedState<DefaultTheme>('theme', light);

  const toggleTheme = () => {
    setTheme(theme.title === 'light' ? dark : light)
  }

  return (
    <ThemeProvider theme={theme}>
      <Particlescom toggleTheme={toggleTheme} />
      <div className='App'>
        <GlobalStyle />
        <Presentation toggleTheme={toggleTheme} />
        <About toggleTheme={toggleTheme} />
        <Technologies toggleTheme={toggleTheme} />
        <Projects toggleTheme={toggleTheme} />
        <Contact toggleTheme={toggleTheme} />
      </div>
      <Footer toggleTheme={toggleTheme} />
    </ThemeProvider>
  );
}

export default App;
