import React, { useContext, useState } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';

import ProjectsData from '../../projects.json';

import {
    ContainerProjects,
    SubContainerProjects,
    Divider,
    SubTitle,
    SubTitleColor,
    ContainerButtons,
    ButtonArrows,
    ButtonGit,
    Carousel,
    Project,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools
} from './styles';

import { FiArrowRight, FiArrowLeft, FiExternalLink } from "react-icons/fi";

import 'keen-slider/keen-slider.min.css';
import { useKeenSlider } from 'keen-slider/react';

interface Props {
    toggleTheme(): void;
}

const Projects: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    const [currentSlide, setCurrentSlide] = useState(0)
    const [loaded, setLoaded] = useState(false)
    const [sliderRef, instanceRef] = useKeenSlider({
        initial: 0,
        slideChanged(slider) {
            setCurrentSlide(slider.track.details.rel)
        },
        created() {
            setLoaded(true)
        },
    })

    return (
        <ContainerProjects id="portifolio">
            <SubContainerProjects>
                <Divider />
                <SubTitle>
                    Meus projetos
                    <SubTitleColor> pessoais</SubTitleColor>
                </SubTitle>

                <ContainerButtons>
                    {loaded && instanceRef.current && (
                        <>
                            <ButtonArrows
                                left
                                onClick={(e) =>
                                    e.stopPropagation() || instanceRef.current?.prev()
                                }
                                disabled={currentSlide === 0}
                            >

                                <FiArrowLeft color='#37D892' size={25} />
                            </ButtonArrows>

                            <Arrow
                                onClick={(e) =>
                                    e.stopPropagation() || instanceRef.current?.next()
                                }
                                disabled={
                                    currentSlide ===
                                    instanceRef.current.track.details.slides.length - 1
                                }
                            />
                        </>
                    )}
                    <ButtonArrows onClick={() => { }}>
                    </ButtonArrows>
                    <ButtonArrows onClick={() => { }}>
                        <FiArrowRight color='#37D892' size={25} />
                    </ButtonArrows>
                </ContainerButtons>
            </SubContainerProjects>

            <Carousel ref={sliderRef}>
                {ProjectsData.map((item) => {
                    const { id, img, title, description, tool, link } = item;
                    return (
                        <Project key={id}>
                            <Image />

                            <ContainerTitle>
                                <TitleProject>{title}</TitleProject>
                                <a href={link}><FiExternalLink color={colors.text} size={25} /></a>
                            </ContainerTitle>

                            <Description>{description}</Description>
                            <Tools>{tool}</Tools>
                        </Project>
                    )
                })}
            </Carousel>
        </ContainerProjects >
    );
}

export default Projects;
