import React, { useContext, useCallback } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import { FaMoon } from "react-icons/fa";
import { FiMenu } from "react-icons/fi";
import { IoMdClose } from "react-icons/io";

import Typewriter from 'typewriter-effect';

import { ContainerPresentation, ContainerSub1, ContainerSub2, ContainerMe, TextName, SubText, Navigations, TextNavs, Svg } from './styles';


interface Props {
    toggleTheme(): void;
}

const Presentation: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    return (
        <ContainerPresentation>
            <ContainerSub>
                <ContainerMe>
                    <TextName>
                        Gabriel Barbosa
                    </TextName>
                    <SubText>
                        <Typewriter
                            options={{
                                autoStart: true,
                                loop: true,
                                delay: 30,
                                strings: [
                                    "< Front End Developer />",
                                    "< Desenvolvedor Front End />"
                                ]
                            }}
                        />

                    </SubText>
                </ContainerMe>

                <Navigations>
                    <TextNavs href="#sobre">Sobre</TextNavs>
                    <TextNavs href="#projetos">Projetos</TextNavs>
                    <TextNavs href="#contato">Contato</TextNavs>
                    <li className='switchCenter'>
                        <Switch
                            onChange={toggleTheme}
                            checked={title === 'light'}
                            checkedIcon={false}
                            uncheckedIcon={false}
                            handleDiameter={20}
                            offHandleColor=""
                            uncheckedHandleIcon={<FaMoon color='#2E2F31' size={20} />}
                            onHandleColor="#DCE0E2"
                            offColor="#DCE0E2"
                            onColor="#2E2F31"
                            activeBoxShadow="0 0 2px 3px #33bbff0"
                        />
                    </li>
                </Navigations>
            </ContainerSub>

            <ContainerSub>
                <Svg viewBox="0 0 800 500" preserveAspectRatio="none" xmlns="http://www.w3.org/2000/svg" width="50%" id="blobSvg">
                    <g transform="translate(142.19391250610352, 9.764085292816162)">
                        <defs>
                            <linearGradient id="gradient" x1="0%" y1="0%" x2="0%" y2="100%">
                                <stop offset="0%" style={{ stopColor: "rgb(255, 95, 109)" }}></stop>
                                <stop offset="100%" style={{ stopColor: "rgb(255, 195, 113)" }}></stop>
                            </linearGradient>
                        </defs>
                        <path className="blob" d="M439,300.5Q407,351,362,373.5Q317,396,269.5,420Q222,444,180.5,411.5Q139,379,100.5,342Q62,305,69.5,252Q77,199,86,135Q95,71,155.5,43Q216,15,276,38.5Q336,62,364,110Q392,158,431.5,204Q471,250,439,300.5Z" fill="url(#gradient)"></path>
                    </g>
                </Svg>
            </ContainerSub>
        </ContainerPresentation >
    );
}

export default Presentation;
