import React, { useContext } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import { Link } from 'react-router-dom';
import ProjectsData from '../../certificates.json';

import {
    ContainerProjects,
    ContainerHorizontal,
    SubContainerProjects,
    Divider,
    SubTitle,
    SubTitleColor,
    ContainerButtons,
    ButtonArrows,
    ButtonGit,
    Projects,
    Project,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools,
    AllProjects,
    TextButton
} from './styles';


import { FiExternalLink } from "react-icons/fi";

interface Props {
    toggleTheme(): void;
}

const Certificates: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    return (
        <ContainerProjects>
            <ContainerHorizontal>
                <SubContainerProjects>
                    <Divider />
                    <SubTitle>
                        Meus
                        <SubTitleColor> certificados</SubTitleColor>
                    </SubTitle>
                </SubContainerProjects>

                <Projects>
                    {ProjectsData.map((item) => {
                        const { id, img } = item;
                        return (
                            <Project key={id}>
                                <Image src={img}/>
                            </Project>
                        )
                    })}
                </Projects>
            </ContainerHorizontal>

            <ContainerHorizontal>
                <AllProjects href='https://github.com/ogabrielbarbosa?tab=repositories'>
                    <TextButton>
                        Ver todos repositórios no GitHub
                    </TextButton>
                </AllProjects>

            </ContainerHorizontal>

        </ContainerProjects>
    );
}

export default Certificates;
