import styled from "styled-components";

export const ContainerContent = styled.div`
    display: flex;
    background-color: #fff;
    margin-left: 400px;
    height: 100vh;
    padding: 30px;
`;

export const Header = styled.div`
    display: flex;
    background-color: #fff;
    height: 15vh;
    width: 100%;
`;

export const TitlePokemon = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 20px;
    color: #fff;
    text-transform: capitalize;
`;

export const IdPokemon = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 20px;
    color: #FF0040;
`;
