import styled from "styled-components";

export const ContainerSidebar = styled.div`
    display: flex;
    width: 400px;
    height: 100vh;
    background-color: #CC0033;  
    padding: 30px;
    flex-direction: column;
`;

export const ContainerPokeball = styled.div`
    display: flex;
    height: 15vh;
    width: 100%;
    justify-content: center;
    align-items: center;
`;

export const ContainerTitle = styled.div`
    display: flex;
    height: 15vh;
    width: 100%;
    flex-direction: column;
`;

export const Title = styled.span`
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 30px;
    color: #fff;
`;

export const SubTitle = styled.span`
    font-family: 'Inter';
font-style: normal;
font-weight: 500;
font-size: 15px;
line-height: 18px;

color: #FFB8B8;
`;
