import React, { useContext } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';

import ProjectsData from '../../projects.json';

import {
    ContainerProjects,
    SubContainerProjects,
    Divider,
    SubTitle,
    SubTitleColor,
    ContainerButtons,
    ButtonArrows,
    ButtonGit,
    Carousel,
    Project,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools
} from './styles';

import { FiArrowRight, FiArrowLeft, FiExternalLink } from "react-icons/fi";

import 'keen-slider/keen-slider.min.css';
import { useKeenSlider } from 'keen-slider/react';

interface Props {
    toggleTheme(): void;
}

const Projects: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);

    const slider = useKeenSlider(
        {
            slideChanged() {
                console.log('slide changed')
            },
        },
        {
            nextSlide(){
                slider.next()
            }
        }
    )

    return (
        <ContainerProjects id="portifolio">
            <SubContainerProjects>
                <Divider />
                <SubTitle>
                    Meus projetos
                    <SubTitleColor> pessoais</SubTitleColor>
                </SubTitle>

                <ContainerButtons>
                    <ButtonArrows onClick={slider.nextSlide}>
                        <FiArrowLeft color='#37D892' size={25} />
                    </ButtonArrows>
                    <ButtonArrows onClick={previousSlide}>
                        <FiArrowRight color='#37D892' size={25} />
                    </ButtonArrows>
                </ContainerButtons>
            </SubContainerProjects>

            <Carousel ref={slider}>
                {ProjectsData.map((item) => {
                    const { id, img, title, description, tool, link } = item;
                    return (
                        <Project key={id}>
                            <Image />

                            <ContainerTitle>
                                <TitleProject>{title}</TitleProject>
                                <a href={link}><FiExternalLink color={colors.text} size={25} /></a>
                            </ContainerTitle>

                            <Description>{description}</Description>
                            <Tools>{tool}</Tools>
                        </Project>
                    )
                })}
            </Carousel>
        </ContainerProjects >
    );
}

export default Projects;
