import React, { useContext, useState, useEffect } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import axios from 'axios';

import { BiSearch } from "react-icons/bi";

import { ContainerContent, Header, TitlePokemon, IdPokemon, ContainerPokemon, ContainerTexts, ContainerImage } from './styles';

interface Props {
    toggleTheme(): void;
}

interface Pokemon {
    name: string;
    sprite: string;
    game_index: string;
    types: Array<{ name: string }>;
}

const Content: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    const [pokemonList, setPokemonList] = useState<Pokemon[]>([]);

    useEffect(() => {
        async function fetchData() {
            let url = 'https://pokeapi.co/api/v2/pokemon/1';

            fetch(url)
        }

        fetchData();
    }, []);

    return (
        <ContainerContent>
            <Header></Header>

            {pokemonList.map((item) => {
                return (
                    <ContainerPokemon key={item.name}>
                        <ContainerTexts>
                            <IdPokemon>{item.game_index}</IdPokemon>
                            <TitlePokemon>{item.name}</TitlePokemon>
                        </ContainerTexts>

                        <ContainerImage>

                        </ContainerImage>
                    </ContainerPokemon>
                );
            })}

        </ContainerContent >
    );
}

export default Content;
