    height: 100vh;
    padding: 30px;
    flex-direction: column;
    gap: 20px;
`;

export const Header = styled.div`
    display: flex;
    background-color: #fff;
    height: 130px;
    width: 100%;
`;

export const ContainerTexts = styled.div`
    display: flex;
    flex-direction: column;
`;

export const TitlePokemon = styled.span`
    font-family: 'Inter';
