import React, { useContext } from 'react';
import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';

import ProjectsData from '../../projects.json';

import {
    ContainerProjects,
    SubContainerProjects,
    Divider,
    SubTitle,
    SubTitleColor,
    ContainerButtons,
    ButtonArrows,
    ButtonGit,
    Carousel,
    Project,
    Image,
    ContainerTitle,
    TitleProject,
    Description,
    Tools
} from './styles';

import { FiArrowRight, FiArrowLeft, FiExternalLink } from "react-icons/fi";
import { Swiper, SwiperSlide, useSwiper } from 'swiper/react';
import { Pagination } from 'swiper';
import 'swiper/css';
import 'swiper/css/pagination';
interface Props {
    toggleTheme(): void;
}

const Projects: React.FC<Props> = ({ toggleTheme }) => {
    const { colors, title } = useContext(ThemeContext);
    const swiper = useSwiper();

    function handleRightClick() {
        swiper.slideNext();
    }

    function handleLeftClick() {
        swiper.slidePrev();
    }

    return (
        <ContainerProjects id="portifolio">

            <Swiper
                slidesPerView={2}
                spaceBetween={30}
                pagination={false}
                modules={[Pagination]}
            >
                <SubContainerProjects>
                    <Divider />
                    <SubTitle>
                        Meus projetos
                        <SubTitleColor> pessoais</SubTitleColor>
                    </SubTitle>

                    <ContainerButtons>
                        <ButtonArrows onClick={handleLeftClick} >
                            <FiArrowLeft color='#37D892' size={25} />
                        </ButtonArrows>
                        <ButtonArrows onClick={handleRightClick}>
                            <FiArrowRight color='#37D892' size={25} />
                        </ButtonArrows>
                    </ContainerButtons>
                </SubContainerProjects>

                <Carousel></Carousel>
                {ProjectsData.map((item) => {
                    const { id, img, title, description, tool, link } = item;
                    return (
                        <SwiperSlide key={id}>
                            <Project>
                                <Image />

                                <ContainerTitle>
                                    <TitleProject>{title}</TitleProject>
                                    <a href={link}><FiExternalLink color={colors.text} size={25} /></a>
                                </ContainerTitle>

                                <Description>{description}</Description>
                                <Tools>{tool}</Tools>
                            </Project>
                        </SwiperSlide >
                    )
                })}
            </Swiper>


            {/**
             * 
            <ButtonGit>
                Ver todos os repositórios no Github
            </ButtonGit>
             */}
        </ContainerProjects>
    );
}

export default Projects;
